import React, { useState, useRef, useEffect } from "react";
import './chatbot.css'; // Correct path for CSS
import logo from 'logo.png'; // Correct path for image

function Chatbot() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const textareaRef = useRef(null); // Reference to the textarea for auto-resize

    // Auto-resize effect for the textarea
    useEffect(() => {
        if (textareaRef.current) {
            textareaRef.current.style.height = 'auto'; // Reset height to recompute
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`; // Set new height
        }
    }, [input]);

    const handleSendMessage = async () => {
        if (input.trim() === "") return;

        const userMessage = { sender: "user", text: input };
        setMessages(prev => [...prev, userMessage]);

        setInput(""); // Clear input after sending

        try {
            const response = await fetch("http://localhost:5000/chat", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ message: input }),
            });

            const data = await response.json();

            const botMessage = { sender: "bot", text: data.response };
            setMessages(prev => [...prev, botMessage]);
        } catch (error) {
            console.error("Error sending message:", error);
            setMessages(prev => [...prev, { sender: "bot", text: "Oops! Something went wrong." }]);
        }
    };

    const handleKeyDown = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault(); // Prevents adding a new line
            handleSendMessage();
        }
    };

    return (
        <div className="chatbot-page">
            <div className="chat-container">
                <header className="chat-header">
                    <img src={logo} alt="Chatbot Logo" className="chat-logo" />
                    <h3>AI & Data Science Chatbot</h3>
                    <img src={logo} alt="Chatbot Logo" className="chat-logo" />
                </header>

                <div className="chat-box">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender}`}>
                            <div className="message-text">{msg.text}</div>
                        </div>
                    ))}
                </div>

                <div className="input-area">
                    <textarea
                        ref={textareaRef}
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={handleKeyDown}
                        placeholder="Type your message..."
                        className="chat-textarea"
                        rows={1} // Start small, grows automatically
                    />
                    <button onClick={handleSendMessage} className="send-button">Send</button>
                </div>
            </div>
        </div>
    );
}

export default Chatbot;
